import numpy as np

# pi and e are from wikipedia

pi = "3."

e = "2."

kha = 16.9190

def test(str):
    freq = np.zeros(10)
    for c in str:
        if c == ".":
            continue

        freq[int(c) - 1] += 1

    n = freq.sum()
    kh2 = ((freq - n / 10) ** 2).sum() / (n / 10)

    print(f"観測度数: {freq}")
    print(f"検定統計量: {kh2}")
    print(f"等確率か?: {kh2 <= kha}")

print("(1)")
print(f"円周率:")
test(pi[2:102])

print(f"\nネイピア数:")
test(e[2:102])

print("\n(2)")
print(f"円周率:")
test(pi)

print(f"\nネイピア数:")
test(e)
